jQuery.fn.jcScrollTop=function(a){a=jQuery.extend({duration:1000,scroleActivateAt:200,scrollElementId:"jcScrollTop",easingType:"easeInOutQuad",position:"right",scrollText:"Scroll to top",backgroundColor:"#c00",foreColor:"#FFF",fontFamily:"calibri",fontSize:"15px",fontWeight:"bold",textPadding:"5px",containerWidth:"120px",containerBorder:"2px solid #960404",borderRadius:"10px 10px 0px 0px",callback:function(){}},a);var b=Math.max(0,(jQuery(window).width()-this.outerWidth())/2+jQuery(window).scrollLeft())+"px";jQuery("body").append("<div id='"+a.scrollElementId+"' style='background:"+a.backgroundColor+";border:"+a.containerBorder+";border-radius:"+a.borderRadius+";position:fixed;bottom:-2px;width:"+a.containerWidth+";font-weight:"+a.fontWeight+";color:"+a.foreColor+";text-align:center;font-family:"+a.fontFamily+";font-size:"+a.fontSize+";cursor:pointer;padding:"+a.textPadding+"'>"+a.scrollText+"</div>");if(a.position=="left")jQuery("#"+a.scrollElementId).css("left","10px");else if(a.position=="center")jQuery("#"+a.scrollElementId).css("left",b);else jQuery("#"+a.scrollElementId).css("right","10px");jQuery("#"+a.scrollElementId).hide().click(function(){jQuery("html,body").animate({scrollTop:0},a.duration,a.easingType,function(){a.callback()})});jQuery(window).scroll(function(){var b=jQuery(window).scrollTop();if(b>a.scroleActivateAt){jQuery("#"+a.scrollElementId).show("slow")}else{jQuery("#"+a.scrollElementId).hide("slow")}})}